--USERNAME: User11--SQL code for the creation of the tables and the addition of data for The Beckenham Walkers database system.--A new schema called 'walkdb' has been created to store all the new tables.create schema walkdb;--Creating the tables for each entity--route, cafe, walk event, instructor, walk attendee, member, meeting point, carpark--Route entitycreate table walkdb.route  (    route_id int not null,    name varchar(50) not null,    difficulty char(1) not null,    length double precision,    cafe_id int not null    );--Unique constraintalter table walkdb.route add constraint route_unique unique(name, difficulty);--Type constraintalter table walkdb.route add constraint difficulty_types check (difficulty = 'E' OR difficulty = 'M' OR difficulty = 'S');--Creates the geometry column to hold the spatial dataalter table walkdb.route add column location geometry(Linestring, 27700);--Primary and foreign keyalter table walkdb.route add constraint route_pkey primary key (route_id);alter table walkdb.route  add constraint route_cafe_fkey foreign key (cafe_id) references walkdb.cafe (cafe_id);--A trigger function to generate the route lengthcreate trigger route_length_update  after insert or update of location on walkdb.route  for each row  execute procedure update_length();create or replace function update_length() returns trigger as $body$  begin  update walkdb.route set length = st_length(location)    where route_id = new.route_id;    return null;    end;    $body$ language 'plpgsql';--Cafe entitycreate table walkdb.cafe  (    cafe_id int not null,    name varchar(50) not null,    postcode varchar(7) not null,    town varchar(20)    );--Unique constraintalter table walkdb.cafe add constraint cafe_unique unique(name, postcode);--Creates the geometry column to hold the spatial dataalter table walkdb.cafe add column location geometry(POLYHEDRALSURFACE, 27700, 3,1);--To force the geometry column to use 3D coordinatesalter table walkdb.cafe   alter column location type geometry(POLYHEDRALSURFACEZ)    using ST_Force_3D(location);--Primary keyalter table walkdb.cafe add constraint cafe_pkey primary key (cafe_id);--Member entitycreate table walkdb.member  (    member_id int not null,    first_name varchar(20) not null,    last_name varchar(20) not null,    dob date not null,    email_address varchar(254) not null,    phone_number varchar(11) not null    );--Unique constraintalter table walkdb.member add constraint member_unique unique(first_name, last_name, phone_number);--Constriant to ensure the member's age is over 18alter table walkdb.member add constraint member_age check (dob < (current_date - interval '18' year));--Primary keyalter table walkdb.member add constraint member_pkey primary key (member_id);--Instructor entitycreate table walkdb.instructor  (    instructor_id int not null,    first_name varchar(20) not null,    last_name varchar(20) not null,    email_address varchar(254) not null,    phone_number varchar(11) not null    );--Unique constraintalter table walkdb.instructor add constraint instructor_unique unique(first_name, last_name, phone_number);--Primary keyalter table walkdb.instructor add constraint instructor_pkey primary key (instructor_id);--Meeting Point entitycreate table walkdb.meeting_point  (    postcode char(7) not null,    name varchar(50) not null,    town varchar(20) not null,    county varchar(15)    carpark_id int not null    );--Creates the geometry column to hold the spatial dataalter table walkdb.meeting_point add column location geometry(Point, 27700);--Primary and foreign keyalter table walkdb.meeting_point add constraint meeting_point_pkey primary key (postcode);alter table walkdb.meeting_point  add constraint meeting_point_carpark_fkey foreign key (carpark_id) references walkdb.carpark (carpark_id);--Carpark entitycreate table walkdb.carpark  (    carpark_id int not null,    name varchar(50) not null,    price_per_hour double precision not null,    postcode varchar(7)    );--Unique constraintalter table walkdb.carpark add constraint carpark_unique unique(name);--Creates the geometry column to hold the spatial dataalter table walkdb.carpark add column location geometry(Polygon, 27700);--Primary keyalter table walkdb.carpark add constraint carpark_pkey primary key (carpark_id);--Walk Event entitycreate table walkdb.walk_event  (    walk_date date not null,    start_time time not null,    route int,    instructor int,    meeting_point char(7)    );--Primary and foreign keysalter table walkdb.walk_event add constraint walk_event_pkey primary key (walk_date);alter table walkdb.walk_event  add constraint walk_event_route_fkey foreign key (route) references walkdb.route (route_id);alter table walkdb.walk_event  add constraint walk_event_instrctor_fkey foreign key (instructor) references walkdb.instructor (instructor_id);alter table walkdb.walk_event  add constraint walk_event_meeting_point_fkey foreign key (meeting_point) references walkdb.meeting_point (postcode);--Walk Attendee entitycreate table walkdb.walk_attendee  (    walk_id date not null,    member_id int not null    );--Unique constraintalter table walkdb.walk_attendee add constraint walk_attendee_unique unique(walk_id, member_id);--Foreign Keysalter table walkdb.walk_attendee add constraint walk_attendee_walk_id_fkey foreign key (walk_id) references walkdb.walk_event (walk_date);alter table walkdb.walk_attendee add constraint walk_attendee_member_id_fkey foreign key (member_id) references walkdb.member (member_id);--Populating each table with a sample of data.--5 MEMBERSinsert into walkdb.member values   (1, 'Nicola', 'Critten', '1995-12-20', 'nicolacritten@hotmail.com', 07756233454),  (2, 'Kate', 'Halpin', '1987-04-17', 'k.halpin@gmail.co.uk', 02086547855),  (3, 'Derek', 'Harvey', '1965-06-26', 'derek_harvey@hotma