_length(a.location)::DECIMAL,2) as "Route Length"from walkdb.route a;--REQUIREMENT 3--Which members are going on a given walk event?select a.member_id as "Member ID",   b.first_name as "Member First Name",  b.last_name as "Member Last Name"from walkdb.walk_attendee a left join  walkdb.member b on a.member_id = b.member_idwhere a.walk_id = '2018-05-05';--REQUIREMENT 4--Which instructor is in charge of each walk event and what is their phone number?select   a.walk_date as "Walk Date", a.instructor as "Instructor ID",  b.first_name as "Instructor First Name", b.last_name as "Instructor Last Name",  b.phone_number as "Instructor Phone Number"from   walkdb.walk_event a left join   walkdb.instructor bon   a.instructor = b.instructor_idgroup by   a.walk_date, b.first_name, b.last_name, b.instructor_id;--REQUIREMENT 5--What is the area of each cafeÌ?select   a.cafe_id as "Cafe ID", a.name as "Cafe Name",   round(st_area(a.location)::DECIMAL,2) as "Cafe Area (m2)"from   walkdb.cafe aorder by   "Cafe Area (m2)";--REQUIREMENT 6--What is the area of each carpark?select   a.carpark_id as "Carpark ID", a.name as "Carpark Name",   round(st_area(a.location)::DECIMAL,2) as "Carpark Area (m2)"from walkdb.carpark aorder by  "Carpark Area (m2)";--REQUIREMENT 7--How many walk events are scheduled for each difficultly?select   b.difficulty as "Difficulty",  count(*) as "Number of Walk Events"from walkdb.walk_event a left join   walkdb.route bon a.route = b.route_idgroup by b.difficultyorder by b.difficulty;--REQUIREMENT 8--How many walk events are being held each month?select  count(*) as "Number of Walk Events",  to_char(a.walk_date, 'Mon') as "Month",    extract(year from a.walk_date) as "Year"from walkdb.walk_event agroup by "Month", "Year"order by "Number of Walk Events";--REQUIREMENT 9--Do any given routes intersect?select  a.route_id as "Route 1 ID",  a.name as "Route 1 Name",  b.route_id as "Route 2 ID",  b.name as "Route 2 Name"from  walkdb.route a,  walkdb.route bwhere  a.route_id < b.route_id and  ST_Intersects(a.location, b.location);--REQUIREMENT 10-- Which meeting points are located within a carpark?select   a.postcode as "Meeting Point Postcode", a.name as "Meeting Point Name",  b.carpark_id as "Carpark ID", b.name as "Carpark Name"from   walkdb.meeting_point a, walkdb.carpark bwhere   st_contains(b.location, a.location) = 't';----REQUIREMENT 11 (extra)--How many members are going on each walk event, what is the route, and who is the instructor?select   a.walk_id as "Walk date", count(a.member_id) as "Number Attending",  c.route_id as "Route", c.name as "Route Name",  b.instructor as "Instructor ID"from   walkdb.walk_attendee a left join  walkdb.walk_event bon   a.walk_id = b.walk_date left join  walkdb.route con b.route = c.route_idgroup by   a.walk_id, b.instructor, c.route_id;